import{_ as m}from"./ValaxyMain.vue_vue_type_style_index_0_lang-2ffa39ea.js";import{_ as l,u as p,c as d,w as a,p as u,o as f,a as o,r as e}from"./app-d1fce95c.js";import"./YunFooter.vue_vue_type_script_setup_true_lang-802859ff.js";import"./YunCard.vue_vue_type_script_setup_true_lang-ff4b468b.js";import"./YunPageHeader.vue_vue_type_script_setup_true_lang-9068a4eb.js";import"./index-463abdac.js";const B=JSON.parse('{"title":"栀子花","description":"","frontmatter":{"title":"栀子花","date":"2024-07-01T00:00:00.000Z","updated":"2024-07-01T00:00:00.000Z","categories":"人生小事","tags":["随记","怀念"],"top":1,"comment":true},"headers":[],"relativePath":"pages/posts/Gardenia.md","path":"/home/runner/work/xbshuai.github.io/xbshuai.github.io/pages/posts/Gardenia.md","lastUpdated":1732072776000}'),n=JSON.parse('{"title":"栀子花","description":"","frontmatter":{"title":"栀子花","date":"2024-07-01T00:00:00.000Z","updated":"2024-07-01T00:00:00.000Z","categories":"人生小事","tags":["随记","怀念"],"top":1,"comment":true},"headers":[],"relativePath":"pages/posts/Gardenia.md","path":"/home/runner/work/xbshuai.github.io/xbshuai.github.io/pages/posts/Gardenia.md","lastUpdated":1732072776000}'),c={name:"pages/posts/Gardenia.md",data(){return{data:n,frontmatter:n.frontmatter,$frontmatter:n.frontmatter}},setup(){const t=p();t.meta.frontmatter=Object.assign(t.meta.frontmatter,n.frontmatter),u("pageData",n)}};function h(t,s,g,$,r,b){const i=m;return f(),d(i,{frontmatter:r.frontmatter,data:r.data},{"main-content-md":a(()=>s[0]||(s[0]=[o("blockquote",null,[o("p",null,"想写这篇很久了，在上周不知道为什么在房间里闻到了一股很像栀子花的味道，于是便动了写篇博客的意思，也是这两个月简单更新一下吧")],-1),o("p",null,"来北京数年了，几乎已经没有再见过栀子花，久到我都快忘了它的味道，那是一种清新花香，记忆里香气范围差不多一米左右。在我初中时，栀子花的花香总是伴着酷热的夏天到来，往往桌子上出现栀子花时，家里已经逐渐打开电风扇了。栀子花对我来说，并没有一段青春伤痛文学，没有那么刻骨铭心的往事，只是我妈很喜欢栀子花。",-1),o("p",null,"我妈喜欢花，这是我大学二年级疫情提前回家才有所了解，那时我妈买了些水仙种子又在二楼客厅里摆了几个花瓶去种，我也打趣到这个年纪就该有些终生爱好，免得以后看不懂电视了无聊。每天下午我妈就要拉我出去散步，去后面水田，我们承包的虾池转转。路上自然是有很多说不出名的野花的，白色穗状的、蓝色星星状的、黄色五角状的，我妈就每样采个几朵带回去插花。再后来就是大四疫情回家了，正值六月栀子花开，我在后厨小桌上见到了多年未见得栀子花，洁白的花色、熟悉的香味一下就让我回到前十年的求学时光。",-1),o("p",null,"我上学早，离家也更早，早在四年级就去了县城读书，那时就一个月回一趟家，后来上了初中，有了大小周，就变成两周回一次。年级越升越高，学业也越来越重，初三开始又是一月回一次。这段时间，家里还在做农资生意，我每次回家也是回爸妈租的小商铺里。商铺破破烂烂的，但也结结实实得承载了我的童年。我记得后院大面积的苔藓，被我妈警告小心滑倒，所以我整个童年也只敢走过一两次，只敢走在干燥的台面上；我记得接待客人的那条长椅，在我五年级时上面贴上了各种农药化肥广告，包装逐渐褪去，只剩下摇摇晃晃的实木椅子；我记得铺上凉席的大床，对着一个小电视，旁边是姑妈家淘汰的电脑桌，上面摆满了各种瓶瓶罐罐，还有洁白的栀子花。",-1),o("p",null,"栀子树铺子在铺子最后面的旱厕旁，在六月回来，有时就会看到我妈摘下几朵栀子花，用水简单冲洗一下，放在电脑桌上，放在收银台上，放在饭桌上。但是啊，我并不喜欢栀子花，我觉得它白色的花瓣上总是会有几个小虫子，冲也冲不走，仿佛扎根在了它的花苞里，还有它实在是太过易逝，逝去时那样不好看，黄色的、枯萎的花瓣在摘下来一天就出现了，扫人兴致的很。我感觉是我妈喜欢的紧，有时我家的栀子花还没开别人的先开了，我妈也会去摘几朵提前放在那些地方，有时我们家的开得盛，我妈也会给隔壁几家送几朵过去。那几年的六月是栀子花和粽叶的香味。",-1),o("p",null,"上了高中后，我参加竞赛，和高三生同步放假时间，回家的机会几乎没有了，农资前景不好，我爸的生意几乎只靠一些老客户维持，我爸妈也准备放弃农资，回老家承包一片水田养虾。于是那个铺子租期到后就不曾再回去过，那颗栀子树也就在那独自开着了，我也不再有如此花香的六月，栀子花离散地点缀在我的后半生里，没有规律，引不起人的注意。",-1),o("p",null,"再次闻到类似的花香味，记忆一下就冲破了封条，我想起我妈、我的和摘下的栀子一样转瞬即逝的童年，不觉有些泪眼婆娑，时间总会淡然，总会美化过去，要活好现在，珍惜现在。",-1),o("blockquote",null,[o("p",null,"栀子花的花语是：纯洁，喜悦，秘密的爱，友谊。")],-1)])),"main-header":a(()=>[e(t.$slots,"main-header")]),"main-header-after":a(()=>[e(t.$slots,"main-header-after")]),"main-nav":a(()=>[e(t.$slots,"main-nav")]),"main-content":a(()=>[e(t.$slots,"main-content")]),"main-content-after":a(()=>[e(t.$slots,"main-content-after")]),"main-nav-before":a(()=>[e(t.$slots,"main-nav-before")]),"main-nav-after":a(()=>[e(t.$slots,"main-nav-after")]),comment:a(()=>[e(t.$slots,"comment")]),footer:a(()=>[e(t.$slots,"footer")]),aside:a(()=>[e(t.$slots,"aside")]),"aside-custom":a(()=>[e(t.$slots,"aside-custom")]),default:a(()=>[e(t.$slots,"default")]),_:3},8,["frontmatter","data"])}const N=l(c,[["render",h]]);export{B as __pageData,N as default};
